// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;
pragma abicoder v2;

import "@balancer-labs/v2-interfaces/contracts/vault/IVault.sol";
import "@balancer-labs/v2-interfaces/contracts/vault/IFlashLoanRecipient.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
// import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/interfaces/IQuoter.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
// import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "hardhat/console.sol";

contract ArbitrageBot is IFlashLoanRecipient {
    IVault public balancerVault;
    ISwapRouter public immutable uniswapV3Router;
    IUniswapV2Router02 public uniswapV2Router;
    IUniswapV2Router02 public sushiswapRouter;
    IQuoter public quoter;


    // Events
    event FlashLoanInitiated(address indexed token, uint256 amount);
    event ArbitrageExecuted(
        address indexed token0,
        address indexed token1,
        uint256 amountToken0,
        uint256 amountToken1
    );
    event SwapExecuted(
        address indexed platform,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut
    );
    event LoanRepaid(address indexed token, uint256 amount);
    event Profit(address indexed token, uint256 profit);

    constructor(
    ) {
        balancerVault = IVault(0xBA12222222228d8Ba445958a75a0704d566BF2C8);
        uniswapV3Router = ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);
        uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        sushiswapRouter = IUniswapV2Router02(0x1b02dA8Cb0d097eB8D57A175b88c7D8b47997506);
        quoter = IQuoter(0xb27308f9F90D607463bb33eA1BeBb41C27CE5AB6);
    }

    function initiateArbitrage(
    ) public {
         IERC20[] memory tokens = new IERC20[](1);
        uint256[] memory amounts = new uint256[](1);
        tokens[0] = IERC20(0x82aF49447D8a07e3bd95BD0d56f35241523fBab1);
        amounts[0] = 10000;
        require(tokens.length == amounts.length, "Tokens and amounts array size mismatch");
        emit FlashLoanInitiated(address(tokens[0]), amounts[0]);

        balancerVault.flashLoan(IFlashLoanRecipient(address(this)), tokens, amounts, abi.encode(0x82aF49447D8a07e3bd95BD0d56f35241523fBab1,0xDA10009cBd5D07dd0CeCc66161FC93D7c9000da1,500));
    }

    function receiveFlashLoan(
        IERC20[] memory tokens,
        uint256[] memory amounts,
        uint256[] memory feeAmounts,
        bytes memory userData
    ) external override {
        require(msg.sender == address(balancerVault), "Callback from unauthorized source");

        IERC20 token = tokens[0];
        uint256 amount = amounts[0];


        (address token0, address token1, uint24 feeTier) = abi.decode(userData, (address, address, uint24));
        console.log("before swap");

        console.log("Check : %d ", token0, feeTier, amount);

        (bool success,uint256 token1Amount) = _trySwapOnUniswapV3(token0, token1, feeTier, amount);

        // if(!success) {
        //     token1Amount = _swapOnUniswapV2(token0, token1, amount);
        // }
        console.log("after swap");
        // // // Now that we have `token1`, we try to sell it for `token0` on SushiSwap for a profit
        uint256 token0Amount = sellOnSushiSwap(token1, token0, token1Amount);

        console.log("Sell on Sushi Swap: ", token0Amount);
        console.log("Balance of : ",IERC20(token0).balanceOf(address(this)));


        // // Repay flash loan
        uint256 amountToRepay = amount + feeAmounts[0];
        IERC20(token).transfer(address(balancerVault), amountToRepay);
        emit LoanRepaid(address(token), amountToRepay);

        console.log("Loan Repaid");

        // // Calculate profits
        uint256 profit = token0Amount - amountToRepay;
        if (profit > 0) {
            IERC20(token0).transfer(msg.sender, profit);
            emit Profit(token0, profit);
            console.log("Profit ");
        }

        console.log("Without Profit");

    }

//    function executeSwap(
//         address token0,
//         address token1,
//         uint24 feeTier,
//         uint256 amountIn
//     ) internal returns (uint256 amountOut) {
//         // Try to swap on Uniswap V3
//         bool success;
//         (success, amountOut) = _trySwapOnUniswapV3(token0, token1, feeTier, amountIn);

//         console.log("Result of v3 swap attempt : ",amountOut);
//         // If Uniswap V3 swap was not successful, try Uniswap V2 swap
//         if (!success) {
//             amountOut = _swapOnUniswapV2(token0, token1, amountIn);
//         }

//         return amountOut;
//     }

    function _trySwapOnUniswapV3(
        address token0,
        address token1,
        uint24 feeTier,
        uint256 amountIn
    ) private returns (bool success, uint256 amountOut) {
        // Prepare parameters for the Uniswap V3 swap
        IERC20(token0).approve(address(uniswapV3Router),amountIn);

        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
            tokenIn: token0,
            tokenOut: token1,
            fee: feeTier,
            recipient: address(this),
            deadline: block.timestamp + 600,
            amountIn: amountIn,
            amountOutMinimum: 1, // This should be set to the minimum amount you'd accept
            sqrtPriceLimitX96: 0
        });
        console.log("Inside the v3 method");
        console.log("Balance of : ",IERC20(token0).balanceOf(address(this)));
        console.log("Allowance : ", IERC20(token0).allowance(address(this), address(uniswapV3Router)));

        // Perform the swap and use try/catch to handle success/failure
       amountOut = uniswapV3Router.exactInputSingle(params);

        console.log("After v3 swap: ", amountOut);

        // console.log("After swap",amountin);
        // if(amount> 0){
        //     console.log("Its working as expected");
        //     return (true,amount);
        // }
        return (true,amountOut);
    }

    function _swapOnUniswapV2(
        address token0,
        address token1,
        uint256 amountIn
    ) private returns (uint256 amountOut) {
        address[] memory path = new address[](2);
        path[0] = token0;
        path[1] = token1;
        IERC20(token0).approve(address(uniswapV2Router),2^256 - 1);
        console.log("Balance of : ",IERC20(token0).balanceOf(address(this)));


        // First estimate the amount out using Uniswap V2 getAmountsOut function
        // uint[] memory amountsOut = uniswapV2Router.getAmountsOut(amountIn, path);


        // require(amountsOut[1] > 0, "Uniswap V2: Insufficient output amount");

        // Execute the swap
        uint[] memory amounts = uniswapV2Router.swapExactTokensForTokens(
            amountIn /10,
            1, // Use the estimated amount out
            path,
            address(this),
            block.timestamp + 600
        );
        console.log("AfterSwap : ");

        amountOut = amounts[1]; // This is the amount of token1 we received
    }


   // Helper function to sell on SushiSwap
    function sellOnSushiSwap(address tokenIn, address tokenOut, uint256 amountIn) internal returns (uint256 amountOut) {
        // Approve the SushiSwap router to spend the tokenIn.
        IERC20(tokenIn).approve(address(sushiswapRouter), amountIn);

        // Define the path as an array of addresses [tokenIn, tokenOut].
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;

        // Fetch the current rate from SushiSwap to determine the minimum amount out to accept for the swap.
        // This is an optional step if you want to specify slippage tolerance.
        uint256[] memory amountsOutMin = sushiswapRouter.getAmountsOut(amountIn, path);
        uint256 amountOutMin = amountsOutMin[1]; // Replace with a calculation based on slippage tolerance if desired.

        // Execute the swap from tokenIn to tokenOut on SushiSwap.
        uint256[] memory amounts = sushiswapRouter.swapExactTokensForTokens(
            amountIn,
            amountOutMin, // This could be set to 0 to skip the check and accept any amount out.
            path,
            address(this), // The tokens will be sent to this contract.
            block.timestamp // Use a reasonable deadline to ensure the transaction is mined in time.
        );

        // The last element of amounts is the amount of tokenOut we received from the swap.
        amountOut = amounts[amounts.length - 1];

        // Emit an event to notify about the swap execution.
        emit SwapExecuted(
            address(sushiswapRouter),
            tokenIn,
            tokenOut,
            amountIn,
            amountOut
        );

        return amountOut;
    }

}


